ALTER TABLE `journeys` ADD PRIMARY KEY (`id`), ADD KEY `journeys_vehicle_foreign` (`vehicle`);

ALTER TABLE `vehicles` ADD PRIMARY KEY (`id`);

ALTER TABLE `journeys` MODIFY `id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=10;

ALTER TABLE `vehicles` MODIFY `id` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=6;

ALTER TABLE `journeys` ADD CONSTRAINT `journeys_vehicle_foreign` FOREIGN KEY (`vehicle`) REFERENCES `vehicles` (`id`);

COMMIT;
